drillDownQuery.query:
SELECT * FROM demographic d INNER JOIN dxresearch dxr ON ( d.demographic_no = dxr.demographic_no)
LEFT JOIN (
SELECT m1.dataField, m1.demographicNo, m1.dateObserved FROM measurements m1 INNER JOIN ( SELECT MAX(id) AS id FROM measurements m JOIN ( SELECT MAX(`name`) AS 'name' FROM measurementMap WHERE loinc_code LIKE ${loinc} GROUP BY `name` ) mm ON (mm.`name` = m.type) WHERE DATE(m.dateObserved) > ${lowerLimit.date} AND m.demographicNo > 0 GROUP BY m.demographicNo ) m2 ON (m1.id = m2.id)
) A1C ON (d.demographic_no = A1C.demographicNo)
WHERE d.patient_status LIKE ${active} AND dxr.dxresearch_code IN ${dxCodes} AND dxr.`status` NOT LIKE "%D%" AND d.demographic_no > 0 AND d.provider_no LIKE ${provider}
