SELECT GROUP_CONCAT(CONCAT(p.date_prescribed,' ',SUBSTRING_INDEX(p.textView, '

', -1)),' 
' ORDER BY p.date_prescribed DESC SEPARATOR '') AS rx FROM prescription p WHERE p.demographic_no=${demographic}

select type, dataField, comments, dateObserved, dateEntered from measurements
        		where demographicNo=${demographic}
        		and (type="OPID" or type="MAMP" or type="BZD" or type="CANB" or type="Uetc")
        		order by dateEntered;

select type, dataField, comments, dateObserved, dateEntered from measurements
        		where demographicNo=${demographic}
        		and (type="RHU" or type="ORDU" or type="MDD" or type="GWS")
        		order by dateEntered;
