	function gup(name, url)
	{
		if (url == null) { url = window.location.href; }
		name = name.replace(/[\[]/,"\\\[").replace(/[\]]/,"\\\]");
		var regexS = "[\\?&]"+name+"=([^&#]*)";
		var regex = new RegExp(regexS);
		var results = regex.exec(url);
		if (results == null) { return ""; }
		else { return results[1]; }
	}
	var demographicNo ="";

